mapper.select:
	select 
	from draw_log T WHERE 1 = 1
      
        AND one_key = #{oneKey,jdbcType=VARCHAR}
      
      
        AND activity_id = #{activityId,jdbcType=BIGINT}
      
      
        AND prize_id = #{prizeId,jdbcType=BIGINT}
      
      
        AND activity_enterprise_no = #{activityEnterpriseNo,jdbcType=VARCHAR}
      
      
        AND prize_enterprise_no = #{prizeEnterpriseNo,jdbcType=VARCHAR}
      
      
        AND create_date = #{createDate,jdbcType=DATE}
      
      
        AND create_time = #{createTime,jdbcType=TIMESTAMP}
