mapper.select:
	select 
	from msg_email_message T WHERE 1 = 1
      
        AND msg_id = #{msgId,jdbcType=VARCHAR}
      
      
        AND user_id = #{userId,jdbcType=BIGINT}
      
      
        AND user_name = #{userName,jdbcType=VARCHAR}
      
      
        AND mail = #{mail,jdbcType=VARCHAR}
      
      
        AND template_id = #{templateId,jdbcType=BIGINT}
      
      
        AND templet_params = #{templetParams,jdbcType=VARCHAR}
      
      
        AND message_title = #{messageTitle,jdbcType=VARCHAR}
      
      
        AND message_content = #{messageContent,jdbcType=VARCHAR}
      
      
        AND channel_Id = #{channelId,jdbcType=VARCHAR}
      
      
        AND msg_status = #{msgStatus,jdbcType=SMALLINT}
      
      
        AND send_ip = #{sendIp,jdbcType=VARCHAR}
      
      
        AND operator_id = #{operatorId,jdbcType=BIGINT}
      
      
        AND operator_name = #{operatorName,jdbcType=VARCHAR}
      
      
        AND create_time = #{createTime,jdbcType=TIMESTAMP}
      
      
        AND update_time = #{updateTime,jdbcType=TIMESTAMP}
