mapper.insert:
insert into msg_phone_channel (id, channel_id, name, 
  port_url, sign_key, system_sign_key, 
  status, create_time, update_time
  )
values (#{id,jdbcType=BIGINT}, #{channelId,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, 
  #{portUrl,jdbcType=VARCHAR}, #{signKey,jdbcType=VARCHAR}, #{systemSignKey,jdbcType=VARCHAR}, 
  #{status,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )

mapper.select:
	select 
	from msg_phone_channel T WHERE 1 = 1
      
        AND channel_id = #{channelId,jdbcType=VARCHAR}
      
      
        AND name = #{name,jdbcType=VARCHAR}
      
      
        AND port_url = #{portUrl,jdbcType=VARCHAR}
      
      
        AND sign_key = #{signKey,jdbcType=VARCHAR}
      
      
        AND system_sign_key = #{systemSignKey,jdbcType=VARCHAR}
      
      
        AND status = #{status,jdbcType=VARCHAR}
      
      
        AND create_time = #{createTime,jdbcType=TIMESTAMP}
      
      
        AND update_time = #{updateTime,jdbcType=TIMESTAMP}
